//now change functional component to class component
class Map4 extends React.Component {
    constructor() {
        super()
        this.state ={
            array : []
        }
    }
    render() {
        function fillArray() {
            this.setState({
                array : [1, 2, 3, 4]
            })
        }
        return(
            <React.Fragment>
                {this.state.array.map(item => (
                    <li>{item}</li>
                ))}
            </React.Fragment>
        )
    }
}

export default Map4
