import React from 'react'

class TableAPI extends React.Component {
    constructor() {
        super();
        this.state ={
        movies : [
            {Name: 'Lion King', Released: 1994, Rating: 8.5, Thumbnail: "lionking.jpg"},
            {Name: 'Up', Released: "", Rating: 8.2, Thumnail: "upmovie.jpg"},
            {Name: 'Apollo 13', Released: 1995, Rating: 7.6, Thumbnail: ""}
        ]
        }
    }
    render() {
        return(
            <React.Fragment>
                <table>
                    <th>Name</th>
                    <th>Released</th>
                    <th>Rating</th>
                    <th>Thumbnail</th>
                    <tr>{this.state.movies.name}</tr>
                </table>
            </React.Fragment>
        )
    }
}

export default TableAPI
