import React from 'react'

//create functional component 
// function Map4() {
//     return(
//         <React.Fragment>
//             <p>hi</p>
//         </React.Fragment>
//     )
// }


//now change functional component to class component
class Map4 extends React.Component {
    constructor() {
        super()
        this.state ={
            array : [1,2,3,4],
            array2: [10, 11, 12]
        }
    }
    fillArray() {
        this.setState({array : [5,6,7,8]})
        this.state.array2.forEach(function(i) {
            i += i;
            console.log(i)
            //doubles i
        })
        this.state.array.find(item => item > 0)
    }
    componentDidMount() {
        this.fillArray()
    }

    render() {


        return(
            <React.Fragment>
                {this.state.array.map(item => (
                    <li>{item}</li>
                ))}
                {console.log(this.state.array)}
            </React.Fragment>
        )
    }
}

export default Map4
